func TestBasement(t *testing.T) {
	for _, test := range tests2 {
		if actual, err := Basement(test.input); actual != test.expected {

			if err != nil {
				continue
			}

			t.Errorf("Convert(%q) = %d, expected %d.",
				test.input, actual, test.expected)
		}
	}
}
